	 (NULL,'animare','<p>Prontuário Iclinic Maio/2022.</p>','2022-05-05 10:52:11.651-03','2022-05-05 10:52:11.651-03','nota_clinica',478,510,'{https://images-telemedicina.s3.us-west-2.amazonaws.com/ukMpxlhyVB0Bs14O.pdf}'),
