	 (NULL,'animare','<p>02/05/2022 2022    FALTA     FALTA</p><p>04/05/2022  022     FALTA      FALTA</p>','2022-05-06 18:22:19.087-03','2022-05-06 18:22:19.087-03','nota_clinica',589,538,'{}'),
